SELECT apellido1, apellido2, nombre FROM persona ORDER BY apellido1, apellido2, nombre;
SELECT apellido1, apellido2, nombre FROM persona WHERE telefono IS NULL;
SELECT apellido1, apellido2, nombre FROM persona WHERE LEFT(fecha_nacimiento,4) = 1999;
SELECT apellido1, apellido2, nombre FROM persona WHERE tipo = 'profesor' AND  telefono IS NULL AND RIGHT(nif,1) = 'k' ;
SELECT nombre FROM asignatura WHERE cuatrimestre = 1 AND  curso = 3 AND id_grado = 7;
SELECT  apellido1, apellido2, persona.nombre, departamento.nombre FROM profesor JOIN persona ON persona.id = profesor.id_profesor JOIN departamento ON profesor.id_departamento = departamento.id ORDER BY apellido1, apellido2, persona.nombre;
SELECT  asignatura.nombre FROM asignatura JOIN curso_escolar ON asignatura.curso = curso_escolar.id JOIN alumno_se_matricula_asignatura ON asignatura.id = alumno_se_matricula_asignatura.id_asignatura JOIN persona ON alumno_se_matricula_asignatura.id_alumno = persona.id WHERE persona.nif = '26902806M';
SELECT departamento.nombre FROM departamento JOIN profesor On departamento.id = profesor.id_departamento JOIN asignatura ON profesor.id_profesor = asignatura.id_profesor JOIN grado ON asignatura.id_grado = grado.id WHERE grado.id = 4 GROUP BY departamento.nombre;
SELECT apellido1, apellido2, nombre FROM persona JOIN alumno_se_matricula_asignatura ON persona.id = alumno_se_matricula_asignatura.id_alumno JOIN curso_escolar ON alumno_se_matricula_asignatura.id_curso_escolar = 5 GROUP BY persona.nombre;
SELECT departamento.nombre, apellido1, apellido2, persona.nombre FROM persona JOIN profesor ON persona.id = profesor.id_profesor LEFT JOIN departamento On profesor.id_departamento = departamento.id ORDER BY departamento.nombre, apellido1, apellido2, persona.nombre;
SELECT departamento.nombre AS 'Nom Departament', apellido1, apellido2, persona.nombre FROM persona JOIN profesor ON persona.id = profesor.id_profesor LEFT JOIN departamento On profesor.id_departamento IS NULL ORDER BY departamento.nombre, apellido1, apellido2, persona.nombre;
SELECT departamento.nombre AS 'Nom Departament' FROM persona JOIN profesor ON persona.id = profesor.id_profesor RIGHT JOIN departamento On profesor.id_departamento = departamento.id WHERE profesor.id_departamento IS NULL ORDER BY departamento.nombre, apellido1, apellido2, persona.nombre;
SELECT apellido1, apellido2, persona.nombre, persona.id FROM persona JOIN profesor ON persona.id = profesor.id_profesor LEFT JOIN asignatura ON profesor.id_profesor = asignatura.id_profesor WHERE asignatura.id_profesor IS NULL ORDER BY apellido1, apellido2, persona.nombre;
SELECT asignatura.nombre FROM persona JOIN profesor ON persona.id = profesor.id_profesor RIGHT JOIN asignatura ON profesor.id_profesor = asignatura.id_profesor WHERE profesor.id_profesor IS NULL ORDER BY asignatura.nombre;
SELECT departamento.nombre FROM profesor JOIN departamento ON profesor.id_departamento = departamento.id JOIN asignatura ON profesor.id_profesor = asignatura.id_profesor LEFT JOIN curso_escolar ON asignatura.curso = curso_escolar.id UNION SELECT departamento.nombre FROM profesor JOIN departamento ON profesor.id_departamento = departamento.id JOIN asignatura ON profesor.id_profesor = asignatura.id_profesor RIGHT JOIN curso_escolar ON asignatura.curso = curso_escolar.id;
SELECT COUNT(*)  FROM persona WHERE persona.tipo = 'alumno';
SELECT COUNT(*)  FROM persona WHERE LEFT(fecha_nacimiento,4) = 1999;
SELECT  departamento.nombre, COUNT(*) as count  FROM profesor JOIN departamento ON profesor.id_departamento = departamento.id GROUP BY departamento.nombre  ORDER BY count DESC;
SELECT  departamento.nombre, COUNT(*) as count  FROM profesor RIGHT JOIN departamento ON profesor.id_departamento = departamento.id GROUP BY departamento.nombre  ORDER BY count DESC;
SELECT  grado.nombre, COUNT(*) as count  FROM grado LEFT JOIN asignatura ON grado.id = asignatura.id_grado GROUP BY grado.nombre  ORDER BY count DESC;
SELECT  grado.nombre, COUNT(*) as count  FROM grado LEFT JOIN asignatura ON grado.id = asignatura.id_grado GROUP BY grado.nombre having count(*) > 40 ORDER BY count DESC;
SELECT  grado.nombre, asignatura.tipo, SUM(asignatura.creditos) as count  FROM grado LEFT JOIN asignatura ON grado.id = asignatura.id_grado GROUP BY grado.nombre;
SELECT curso_escolar.anyo_inicio, COUNT(*) as count  FROM curso_escolar LEFT JOIN alumno_se_matricula_asignatura ON curso_escolar.id = alumno_se_matricula_asignatura.id_curso_escolar GROUP BY curso_escolar.id ORDER BY count DESC;
SELECT persona.id, persona.nombre, persona.apellido1, persona.apellido2, COUNT(*) as count  FROM persona LEFT JOIN asignatura ON persona.id = asignatura.id_profesor WHERE persona.tipo = 'profesor' GROUP BY persona.id ORDER BY count DESC;
SELECT persona.id, persona.nif, persona.nombre, persona.apellido1, persona.apellido2, persona.ciudad, persona.direccion, persona.telefono,  MIN(fecha_nacimiento), persona.sexo FROM persona WHERE persona.tipo = 'alumno';
SELECT persona.apellido1, persona.apellido2, persona.nombre FROM persona JOIN profesor ON persona.id = profesor.id_departamento LEFT JOIN asignatura ON asignatura.id = NULL;